INSERT INTO `auth_user_group` (`id`, `uniq_name`, `name`, `comment`, `priority`, `active`) VALUES
(1, 'payer_juridical', 'Юридическое лицо', 'При регистрации доменного имени на юридическое лицо, все права будут принадлежать данному юридическому лицу. Передача прав другому лицу, перевод доменного имени к другому регистратору, удаление доменного имени осуществляются на основании официального письма на бланке организации с подписью директора и печатью юридического лица. Также в некоторых случаях может потребоваться надлежащим образом оформленная доверенность. Юридическое лицо оплачивает услуги только путем безналичного перечисления на основании выставленного счета-фактуры.', 2, 1),
(2, 'payer_phisical', 'Физическое лицо', 'При регистрации доменного имени на физическое лицо, все права на доменное имя принадлежат этому физическому лицу. Передача прав другому лицу, перевод доменного имени к другому регистратору, удаление доменного имени, осуществляются за подписью владельца домена, с подтверждением копией удостоверяющего документа (паспорта). Физическое лицо имеет возможность оплачивать услуги любым из указанных на нашем сайте способов оплаты.', 1, 1),
(3, 'payer_singletax', 'Плательщика единого налога или частный предприниматель', 'При регистрации доменного имени на частного предпринимателя, все права на доменное имя принадлежат этому частному предпринимателю. Передача прав другому лицу, перевод доменного имени к другому регистратору, удаление доменного имени, осуществляются за подписью владельца, с приложеной копией удостоверяющего документа (паспорта). Частный предприниматель имеет возможность оплачивать услуги любым из указанных на нашем сайте способов оплаты.', 3, 1)_;_
