(15, 0, 'Klienti-hostinga', 'Клиенты хостинга', NULL, 'true', 0, 'registered', 'select distinct tb_user.email\r\nfrom auth_user as tb_user\r\ninner join hosting_account as tb_account on tb_account.user_id=tb_user.id \r\nwhere tb_account.active=''true'' and tb_account.valid_untill>now()-interval 10 day and tb_user.active=1', 2592000, 1),
(16, 0, '', 'Партнёры хостинга', NULL, 'true', 0, 'checked', NULL, 2592000, 4),
(17, 0, '', 'Клиенты "Дельта-Х"', NULL, 'true', 0, 'registered', NULL, 2592000, 5),
(18, 0, '', 'Тестовая для администраторов', NULL, 'true', 1, 'registered', '', 2592000, 6),
(21, 15, 'Raspolozhennie-na-we', 'Расположенные на web6', NULL, 'true', 0, 'registered', 'SELECT tb_user.email\r\nFROM hosting_server tb_server\r\nINNER JOIN hosting_account tb_account ON (tb_server.id = tb_account.server_id)\r\nINNER JOIN auth_user tb_user ON (tb_account.user_id = tb_user.id)\r\nWHERE\r\n  tb_server.name_fqdn = ''web6.ukraine.com.ua'' AND \r\n  tb_account.active = ''true'' AND \r\n  tb_account.valid_untill >= now()                                 \r\nGROUP BY tb_user.email', 3600, 2),
(22, 15, 'Raspolozhennie-na-we', 'Расположенные на web5', NULL, 'true', 0, 'registered', 'SELECT tb_user.email\r\nFROM hosting_server tb_server\r\nINNER JOIN hosting_account tb_account ON (tb_server.id = tb_account.server_id)\r\nINNER JOIN auth_user tb_user ON (tb_account.user_id = tb_user.id)\r\nWHERE\r\n  tb_server.name_fqdn = ''web5.ukraine.com.ua'' AND \r\n  tb_account.active = ''true'' AND \r\n  tb_account.valid_untill >= now()                                 \r\nGROUP BY tb_user.email', 2592000, 1),
(23, 15, 'Raspolozhennie-na-we', 'Расположенные на web8', NULL, 'true', 0, 'registered', 'SELECT tb_user.email\r\nFROM hosting_server tb_server\r\nINNER JOIN hosting_account tb_account ON (tb_server.id = tb_account.server_id)\r\nINNER JOIN auth_user tb_user ON (tb_account.user_id = tb_user.id)\r\nWHERE\r\n  tb_server.name_fqdn = ''web8.ukraine.com.ua'' AND \r\n  tb_account.active = ''true'' AND \r\n  tb_account.valid_untill >= now()                                 \r\nGROUP BY tb_user.email', 2592000, 3),
(24, 15, '', 'Превышение нагрузки MySQL за последние 5 дней', 'Пользователи, которые превысили нагрузку на MySQL более 10 раз за последние 30 дней и последнее нарушение было не менее 5 дней назад.', 'true', 0, 'registered', 'SELECT \r\n  count(tb_stat.username) as event_count,\r\n  tb_stat.username db_name,\r\n  tb_user.email,\r\n  tb_user.name,\r\n  max(tb_stat.select_questions) max_select,\r\n  min(tb_stat.select_questions) min_select,\r\n  date(min(tb_stat.tstamp)) as min_date,\r\n  date(max(tb_stat.tstamp)) as max_date\r\nFROM `hosting_mysql_user_overload` as tb_stat\r\ninner join hosting_account as tb_account on tb_account.id=tb_stat.account_id\r\ninner join auth_user as tb_user on tb_user.id=tb_account.user_id\r\nwhere tb_stat.tstamp > now() - interval 30 day\r\ngroup by tb_stat.username\r\nhaving max(tb_stat.tstamp) > current_date() - interval 5 day and event_count > 10\r\norder by event_count desc', 2592000, 20),
(25, 15, 'Kotorie-ispolzuyut-B', 'Которые используют БД на web4', NULL, 'true', 0, 'registered', 'SELECT distinct \r\n  tb_user.email\r\nFROM\r\n  hosting_account tb_account\r\n  INNER JOIN hosting_mysql_db tb_db ON (tb_account.id = tb_db.account_id)\r\n  INNER JOIN hosting_server tb_server ON (tb_db.server_id = tb_server.id)\r\n  INNER JOIN auth_user tb_user ON (tb_account.user_id = tb_user.id)\r\n  INNER JOIN hosting_server tb_main_server ON (tb_server.server_id = tb_main_server.id)\r\nWHERE\r\n  tb_account.active = ''true'' AND \r\n  tb_account.valid_untill >= now() AND \r\n  tb_main_server.name_fqdn = ''web4.ukraine.com.ua''', 2592000, 4),
(26, 15, 'Vladeltsi-VPS-na-web', 'Владельцы VPS на web10', NULL, 'true', 0, 'registered', 'select tb_user.email\r\nfrom auth_user tb_user\r\ninner join hosting_vps tb_vps on tb_vps.user_id = tb_user.id\r\ninner join hosting_server tb_server on tb_vps.server_id = tb_server.id\r\nwhere tb_server.uniq_name = ''web10.ukraine.com.ua'' and tb_vps.valid_untill >= current_date() - interval 7 day', 2592000, 13),
(28, 15, '', 'Выделенные сервера в ДЦ Воля', NULL, 'true', 0, 'registered', 'SELECT \r\n  tb_order.email,\r\n  tb_server.data_centre,\r\n  tb_order.name\r\nFROM\r\n  hosting_dedicated_server tb_server\r\n  INNER JOIN hosting_dedicated_order tb_order ON (tb_server.id = tb_order.server_id)\r\nWHERE\r\n  tb_server.data_centre = ''volia''', 2592000, 18),
(29, 15, 'Vladeltsi-VPS-na-web', 'Владельцы VPS на web16', NULL, 'false', 0, 'registered', 'select tb_user.email\r\nfrom auth_user tb_user\r\ninner join hosting_vps tb_vps on tb_vps.user_id = tb_user.id\r\ninner join hosting_server tb_server on tb_vps.server_id = tb_server.id\r\nwhere tb_server.uniq_name = ''web16.ukraine.com.ua'' and tb_vps.valid_untill >= current_date() - interval 7 day', 2592000, 15),
(30, 0, '', 'Клиенты хостинга, которые не оплатили услуги', NULL, 'false', 0, 'registered', 'select \r\n    tb_user.name, \r\n    tb_user.email, \r\n    date(tb_user.registration_dtime) as registration_date,\r\n    (select max(t_account.valid_untill) from hosting_account as t_account where t_account.user_id=tb_user.id) as account,\r\n    (select max(t_domain.valid_untill) from hosting_dns_domain as t_domain where t_domain.user_id=tb_user.id) as domain,\r\n    (select max(t_vps.valid_untill) from hosting_vps as t_vps where t_vps.user_id=tb_user.id) as vps,\r\n    (select max(t_dedicated.paid_untill) from hosting_dedicated_order as t_dedicated where t_dedicated.user_id=tb_user.id) as dedicated\r\nfrom auth_user as tb_user\r\nwhere \r\n	month(tb_user.registration_dtime) = month(now() - interval 1 month) and\r\n    year(tb_user.registration_dtime) = year(now())\r\nhaving \r\n	(account < registration_date + interval 10 day or account is null)\r\n    and (domain < registration_date + interval 10 day or domain is null)\r\n    and (vps < registration_date + interval 14 day or vps is null)\r\n    and (dedicated < registration_date + interval 14 day or dedicated is null)', 2592000, 7),
